import React, {useState} from "react";
import { useNavigate } from "react-router-dom"

export const RegisterForm = (props) => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [register, setRegister] = useState('');
    const [accountMessage, setAccountMessage] = useState('Already have an account? Login here.')

    const handleSubmit = (e) => {
        e.preventDefault();
        let success = false
        const payload = JSON.stringify({
            first_name: firstName,
            last_name: lastName,
            email: email,
            pass_word: pass
        })
        try {
            fetch('http://localhost:8000/users/',{
            method: 'POST',
            body: payload,
            headers:{
                'Content-Type': 'application/json'
                }
            }).then(res => {
                if(!res.ok) {
                  return res.text().then(text => { throw new Error(text) })
                 }
                else {
                 return res.json();
               }    
              })
              .catch(err => {
                 console.log('caught it!',err);
                 success = false
              });
            if (success) {
                setEmail('')
                setFirstName('')
                setLastName('')
                setPass('')
                setRegister('Registered Successfully!')
                setAccountMessage('Click here to Login.')
            }
            else{
                setRegister('Error Registering - Check your information and try again')
                setAccountMessage('Already have an account? Login here.')
            }
        } catch (error) {
            console.error(error);
            setRegister('Error Registering - Check your information and try again')
            setAccountMessage('Already have an account? Login here.')
        }

    }

    return (
        <div >
            <h2>Register</h2>
            <form className="register-form" onSubmit={handleSubmit}>
                <label htmlFor="firstName">First Name</label>
                <input required value={firstName} onChange={(e) => setFirstName(e.target.value)} type="text" placeholder="First Name" id="firstName" name="firstName"/>
                <label htmlFor="lastName">Last Name</label>
                <input required value={lastName} onChange={(e) => setLastName(e.target.value)} type="text" placeholder="Last Name" id="lastName" name="lastName"/>
                <label htmlFor="email">Email</label>
                <input required value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="email@email.com" id="email" name="email"/>
                <label htmlFor="password">Password</label>
                <input required value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder="*********" id="password" name="password"/>
                <button type="submit">Register</button>
                <p className="text-success"><b>{register}</b></p>
            </form>

            <button className="link-btn" onClick={() => navigate('/login')}>{accountMessage}</button>

        </div>
    )
}

export default RegisterForm;
