import React, {useState} from "react";
import { useNavigate } from "react-router-dom"

export const RegisterForm = (props) => {
    const navigate = useNavigate();
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [register, setRegister] = useState('');
    const [error, setError] = useState('');
    const errorDiv = error ? {error} : '';

    const handleSubmit = (e) => {
        e.preventDefault();
        setError(null)
        let success = false
        let errorText = ''
        const payload = JSON.stringify({
            first_name: firstName,
            last_name: lastName,
            email: email,
            pass_word: pass
        })
        try {
            fetch('http://localhost:8000/users/',{
            method: 'POST',
            body: payload,
            headers:{
                'Content-Type': 'application/json'
                }
            }).then(res => {
                    if (!res.ok){
                        errorText = "Error: " + res.status + ' - '
                        success = false
                        return res.json()
                    }
                    else return res.json()
                  //throw new Error('error')
                 }
              ).then(data => {
                if (!success) {
                    for (const err in data.errors){
                    console.log(data.errors[err])
                    for (const msg in data.errors[err]) errorText += data.errors[err][msg]
                    }
                }
                console.log(errorText)
            })
            .catch(error => console.error(error));
            //   .catch(err => {
            //      setError(err)
            //      success = false
            //   });
            if (success) {
                setEmail('')
                setFirstName('')
                setLastName('')
                setPass('')
                setRegister('Registered Successfully!')
                setAccountMessage('Click here to Login.')
            }
            else{
                setRegister(errorText)
            }
        } catch (error) {
            console.error(error);
            setRegister('Error Registering - Check your information and try again')
            setAccountMessage('Already have an account? Login here.')
        }

    }

    return (
        <div >
            <h2>Register</h2>
            <form className="register-form" onSubmit={handleSubmit}>
                <label htmlFor="firstName">First Name</label>
                <input required value={firstName} onChange={(e) => setFirstName(e.target.value)} type="text" placeholder="First Name" id="firstName" name="firstName"/>
                <label htmlFor="lastName">Last Name</label>
                <input required value={lastName} onChange={(e) => setLastName(e.target.value)} type="text" placeholder="Last Name" id="lastName" name="lastName"/>
                <label htmlFor="email">Email</label>
                <input required value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="email@email.com" id="email" name="email"/>
                <label htmlFor="password">Password</label>
                <input required value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder="*********" id="password" name="password"/>
                <button type="submit">Register</button>
                <p className="text-success"><b>{register}</b></p>
            </form>
            {errorDiv}
            <button className="link-btn" onClick={() => navigate('/login')}>Already have an account? Login here.</button>

        </div>
    )
}

export default RegisterForm;
